template.value:
Future<void> postUpdatePassword(String newPassword, String password) {
    final completer = Completer<void>();
    MemberApis.memberPasswordUpdatePost(
      parameters: {'newPassword': newPassword, 'password': password},
      success: (MemberPasswordUpdatePostRespModel res) => completer.complete(),
      fail: (code, msg) => completer.completeError(msg),
    );
    return completer.future;
  }

  Future<bool> gameMaintainMyCollectPost(
      int i, String gameId, String gameTypeId) {
    final completer = Completer<bool>();

    GameApis.gameMaintainMyCollectPost(
        parameters: {
          "type": i,
          "gameId": gameId,
        },
        success: (GameMaintainMyCollectPostRespModel resp) {
          if (gameTypeId != "Recommend") {
            homeGameController.updateGameMap(gameTypeId);
          } else {
            gameModuleRecommendPost();
          }

          completer.complete(true);
        },
        fail: (int code, String message) {
          showToast("${message}");
          completer.complete(false);
        });
    return completer.future;
  }
