Timer? kickTimer;
int count = 0;

kickTimer?.cancel();
kickTimer = null;

var kickAction = (timer) {
  if (count >= 5) {
    kickTimer?.cancel();
    kickTimer = null;
    count = 0;
  }
  count++;
};

kickTimer = Timer.periodic(const Duration(milliseconds: 500), kickAction);
kickAction(kickTimer!); //立即触发timer

