Future<String> readJsonFile(String jsonPath) async {
  File jsonFile = File(jsonPath);
  if (jsonFile.existsSync()) {
    String contents = await jsonFile.readAsString();
    return contents;
  } else {
    // print("not found json file");
    return "";
  }
}
Map<String, dynamic> json = jsonDecode(jsonStr);

import 'package:shared_preferences/shared_preferences.dart';
String localAppMapKey = "localAppMapKey";
Map<String, dynamic> localAppMap = {};
saveLocalApp(MiniAppServerConfig config) async {
  String configStr = jsonEncode(config.toJson());
  localAppMap[config.appId] = configStr;
  SharedPreferences prefs = await SharedPreferences.getInstance();
  String localAppMapStr = jsonEncode(localAppMap);
  await prefs.setString(localAppMapKey, localAppMapStr);
}
_getLocalAppMap() async {
  SharedPreferences prefs = await SharedPreferences.getInstance();
  String? localAppMapStr = prefs.getString(localAppMapKey);
  if (localAppMapStr != null) {
    localAppMap = jsonDecode(localAppMapStr);
  }

  //add to appPageModels
  for (var key in localAppMap.keys) {
    String configStr = localAppMap[key];
    Map<String, dynamic> configMap = jsonDecode(configStr);
    MiniAppServerConfig config = MiniAppServerConfig.fromJson(configMap);
    MiniAppPageModel model = MiniAppPageModel(config);
    pageModels.add(model);
    // await model.updateFiles();
  }
}

