                URLRequest(url: Uri.parse('https://www.baidu.com')),
            onWebViewCreated: (InAppWebViewController controller) {
              _webViewController = controller;
            },
            onLoadStart: (InAppWebViewController controller, Uri? url) {},
            onLoadStop:
                (InAppWebViewController controller, Uri? url) async {},
          ),
        ),
        Positioned(
            bottom: 0,
            left: 30,
            child: Column(
              children: [
                GestureDetector(
                  onTap: () {
                    _capturePng();
                  },
                  child: bytes == null
                      ? Container(
                          color: Colors.orange,
                          child: Text('点击以截屏'),
                        )
                      : Container(
                          width: 375,
                          height: 375,
                          decoration: BoxDecoration(
                              color: Colors.red,
                              border:
                                  Border.all(width: 1, color: Colors.orange)),
                          child: bytes != null
                              ? Image.memory(
                                  bytes!,
                                  width: 375,
                                  height: 375,
                                )
                              : null),
                ),
              ],
            ))
      ],
    ),
  );
}

