String generateNonce(int length) {
  const chars =
      'abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789';
  final random = Random();
  final buffer = StringBuffer();
  for (var i = 0; i < length; i++) {
    buffer.write(chars[random.nextInt(chars.length)]);
  }
  return buffer.toString();
}

// import 'package:archive/archive.dart';
// import 'dart:io';
Future<String> unzip(String zipFilePath, String savePath) async {
String folder = "";
var saveDir = savePath.isNotEmpty
    ? savePath
    : zipFilePath.substring(0, zipFilePath.lastIndexOf("/"));
List<int> bytes = await File(zipFilePath).readAsBytes();
final archive = ZipDecoder().decodeBytes(bytes);
for (final file in archive) {
  String fn = file.name;
  if (file.isFile) {
    if ((!fn.contains("__MACOSX")) && (!fn.contains(".DS_Store"))) {
      if (folder.isEmpty) {
        List<String> list = fn.split("/");
        folder = list[0];
      }
      final data = file.content as List<int>;
      var fp = File("$saveDir/$fn");
      await fp.create(recursive: true);
      await fp.writeAsBytes(data);
    }
  } else {
    if (folder.isEmpty && fn != "__MACOSX/") {
      folder = fn;
      await Directory("$saveDir/$fn").create(recursive: true);
    }
  }
}
return Future.value(folder);
}

