extension DateTimeFromTimeOfDay on DateTime {
  DateTime appliedFromTimeOfDay(TimeOfDay timeOfDay) {
    return DateTime.utc(year, month, day, timeOfDay.hour, timeOfDay.minute);
  }
  DateTime get dayStart{
    return appliedFromTimeOfDay(const TimeOfDay(hour: 0, minute: 0));
  }
  DateTime get dayEnd{
    return appliedFromTimeOfDay(const TimeOfDay(hour: 24, minute: 0));
  }
}

