extension WidthCount on String {
  double get widthUnit {
    String value = this;
    double count = 0;
    for (var i = 0; i < value.length; i++) {
      if (value[i].toString().isChinese) {
        count += 2;
      } else if (value[i].toString().isUpChar) {
        count += 1.5;
      } else {
        count += 1;
      }
    }
    return count;
  }

  bool get isChinese {
    RegExp reg = RegExp("[\u4E00-\u9FFF]|[\uFE30-\uFFA0]");
    return reg.hasMatch(this);
  }

  bool get isUpChar {
    RegExp reg = RegExp("[A-Z]");
    return reg.hasMatch(this);
  }
}

///  需要第三方库crypto:
extension MD5Ext on String {
  String get md5Str {
    Uint8List content = const Utf8Encoder().convert(this);
    Digest digest = md5.convert(content);
    return digest.toString();
  }
}

class _DragPage extends StatefulWidget {
  @override
  _DragPageState createState() => _DragPageState();
}

class _DragPageState extends State<_DragPage> with SingleTickerProviderStateMixin {
  double _top = 0.0; //距顶部的偏移
  double _left = 0.0; //距左边的偏移

  @override
  Widget build(BuildContext context) {
    return Stack(
      children: <Widget>[
        Positioned(
          top: _top,
          left: _left,
          child: GestureDetector(
            child: CircleAvatar(child: Text("A")),
            //手指按下时会触发此回调
            onPanDown: (DragDownDetails e) {
              //打印手指按下的位置(相对于屏幕)
              print("用户手指按下：${e.globalPosition}");
            },
            //手指滑动时会触发此回调
            onPanUpdate: (DragUpdateDetails e) {
              //用户手指滑动时，更新偏移，重新构建
              setState(() {
                _left += e.delta.dx;
                _top += e.delta.dy;
              });
            },
            onPanEnd: (DragEndDetails e) {
              //打印滑动结束时在x、y轴上的速度
              print(e.velocity);
            },
          ),
        )
      ],
    );
  }
}

@override
void didChangeDependencies() {
//在任何statfulwidget里实现这个方法即可缓存图片留给后面的页面直接使用
  super.didChangeDependencies();
  precacheImage(const AssetImage('assets/bg1.png'), context);
  precacheImage(const AssetImage('assets/bg2.jpg'), context);
}

late PointerDownEvent lastOpm;
Widget createContentView() {
  return Listener(
    onPointerDown: (opm) {
      lastOpm = opm;
    },
    onPointerUp: (opm) {
      if (lastOpm.localPosition.dy > opm.localPosition.dy + 10) {
        //手指上划了
        print('上划');
      } else {
        //其他方向
        print('其他方向');
      }
    },
    child: GestureDetector(
      onTap: () {},
      onHorizontalDragEnd: (DragEndDetails details) {},
      behavior: HitTestBehavior.translucent,
      child: CustomScrollView(
        slivers: [
          SliverList(
              delegate: SliverChildBuilderDelegate((context, index) {
            return Container(
              child: Text('$index'),
            );
          }, childCount: 100, addRepaintBoundaries: true))
        ],
      ),
    ),
  );
}

class CircleHalo extends StatefulWidget {
  const CircleHalo({Key? key}) : super(key: key);

  @override
  _CircleHaloState createState() => _CircleHaloState();
}

class _CircleHaloState extends State<CircleHalo>
    with SingleTickerProviderStateMixin {
  late AnimationController _ctrl;

  @override
  void initState() {
    super.initState();
    _ctrl = AnimationController(
      vsync: this,
      duration: Duration(seconds: 2),
    );
    _ctrl.repeat();
  }

  @override
  void dispose() {
    _ctrl.dispose();
    super.dispose();
  }

  @override
  Widget build(BuildContext context) {
    return CustomPaint(
      size: Size(200, 200),
      painter: CircleHaloPainter(_ctrl),
    );
  }
}

class CircleHaloPainter extends CustomPainter {
  Animation<double> animation;

  CircleHaloPainter(this.animation) : super(repaint: animation);

  final Animatable<double> rotateTween = Tween<double>(begin: 0, end: 2 * pi)
      .chain(CurveTween(curve: Curves.easeIn));

  final Animatable<double> breatheTween = TweenSequence<double>(
    <TweenSequenceItem<double>>[
      TweenSequenceItem<double>(
        tween: Tween<double>(begin: 0, end: 4),
        weight: 1,
      ),
      TweenSequenceItem<double>(
        tween: Tween<double>(begin: 4, end: 0),
        weight: 1,
      ),
    ],
  ).chain(CurveTween(curve: Curves.decelerate));

  @override
  void paint(Canvas canvas, Size size) {
    canvas.translate(size.width / 2, size.height / 2);
    final Paint paint = Paint()
      ..strokeWidth = 1
      ..style = PaintingStyle.stroke;

    Path circlePath = Path()
      ..addOval(Rect.fromCenter(center: Offset(0, 0), width: 100, height: 100));
    Path circlePath2 = Path()
      ..addOval(
          Rect.fromCenter(center: Offset(-1, 0), width: 100, height: 100));
    Path result =
        Path.combine(PathOperation.difference, circlePath, circlePath2);

    List<Color> colors = [
      Color(0xFFF60C0C),
      Color(0xFFF3B913),
      Color(0xFFE7F716),
      Color(0xFF3DF30B),
      Color(0xFF0DF6EF),
      Color(0xFF0829FB),
      Color(0xFFB709F4),
    ];

    colors.addAll(colors.reversed.toList());
    final List<double> pos =
        List.generate(colors.length, (index) => index / colors.length);

    paint.shader =
        ui.Gradient.sweep(Offset.zero, colors, pos, TileMode.clamp, 0, 2 * pi);

    paint.maskFilter =
        MaskFilter.blur(BlurStyle.solid, breatheTween.evaluate(animation));
    canvas.drawPath(circlePath, paint);

    canvas.save();
    canvas.rotate(animation.value * 2 * pi);
    paint
      ..style = PaintingStyle.fill
      ..color = Color(0xff00abf2);
    paint.shader = null;
    canvas.drawPath(result, paint);
    canvas.restore();
  }

  @override
  bool shouldRepaint(covariant CircleHaloPainter oldDelegate) =>
      oldDelegate.animation != animation;
}

enum Status{
  ToBeSent(value: 0, label: "待发送", color: Color(0xffb4b4b4)),
  HasBeenSent(value: 1, label: "已发送", color: Color(0xfff79d8c)),
  Received(value: 2, label: "已接收", color: Color(0xff00b9f1)),
  Replied(value: 3, label: "已回复", color: Color(0xff2699f6)),
  Canceled(value: -1, label: "已取消", color: Color(0xff75d239));

  final int value;
  final String label;
  final Color color;

  const Status({
    required this.value,
    required this.label,
    required this.color,
  });


  /// 解析从后台传来的值
  static Status parse(int i) {
    if (i == -1) return Status.Canceled;
    return Status.values[i];
  }
}

import 'package:flutter/material.dart';

class GradientAnimationText extends StatefulWidget {
  // data members
  final Text text;
  final List<Color> colors;
  final Duration duration;
  final bool? reverse;
  final GradientTransform? transform;

  // constructor
  const GradientAnimationText({
    required this.text,
    required this.colors,
    required this.duration,
    this.reverse,
    this.transform,
    super.key,
  });

  @override
  State<GradientAnimationText> createState() => _GradientAnimationTextState();
}

class _GradientAnimationTextState extends State<GradientAnimationText> with SingleTickerProviderStateMixin {
  // data members
  late AnimationController _animationController;
  late Animation _animation;
  late List<Color> colors;
  late int n;
  late double diff;
  late Text text;

  @override
  void initState() {
    super.initState();
    text = widget.text;
    // animation controller
    _animationController = AnimationController(
      vsync: this,
      duration: widget.duration,
    );

    // repeating animation
    _animationController.repeat(reverse: widget.reverse ?? false);

    // animation
    _animation = Tween(begin: 0.0, end: 1.0).animate(_animationController)
      ..addListener(() {
        setState(() {});
      });

    // colors
    colors = [];
    colors.add(widget.colors.last);
    colors.addAll(widget.colors);
    colors.addAll(widget.colors);

    // colors length
    n = widget.colors.length;

    // diff
    diff = (1 / n);
  }

  // creates stops dynamically based on the animation value
  List<double> stopsList() {
    int multiplier = -1 * n;
    List<double> stops = [];

    // generates (2*n + 1) stops
    while (multiplier <= n) {
      stops.add(_animation.value + (multiplier * diff));
      multiplier++;
    }

    return stops;
  }

  @override
  void dispose() {
    // disposing animationController
    _animationController.dispose();
    super.dispose();
  }

  @override
  Widget build(BuildContext context) {
    return ShaderMask(
      // copying the text with color as Colors.white
      child: Text(
        text.data!,
        key: text.key,
        locale: text.locale,
        maxLines: text.maxLines,
        overflow: text.overflow,
        textAlign: text.textAlign,
        selectionColor: text.selectionColor,
        semanticsLabel: text.semanticsLabel,
        softWrap: text.softWrap,
        strutStyle: text.strutStyle,
        style: (text.style != null) ? text.style!.copyWith(color: Colors.white) : const TextStyle(color: Colors.white),
        textDirection: text.textDirection,
        textHeightBehavior: text.textHeightBehavior,
        textScaleFactor: text.textScaleFactor,
        textWidthBasis: text.textWidthBasis,
      ),
      shaderCallback: (rect) {
        // gradient
        return LinearGradient(
          tileMode: TileMode.clamp,
          transform: widget.transform,
          stops: stopsList(),
          colors: colors,
        ).createShader(rect);
      },
    );
  }
}

