screenListener.addScreenRecordListener((recorded) {
  setState(() {
    text = recorded ? "Start Recording" : "Stop Recording";
  });
});
screenListener.addScreenShotListener((filePath) {
  setState(() {
    text = "Screenshot stored on : $filePath";
  });
});
