import 'package:flutter/material.dart';
import 'package:flutter_inappwebview/flutter_inappwebview.dart';

void main() {
  runApp(const MyApp());
}

class MyApp extends StatelessWidget {
  const MyApp({Key? key}) : super(key: key);

  // This widget is the root of your application.
  @override
  Widget build(BuildContext context) {
    return MaterialApp(
      title: 'Flutter Demo',
      theme: ThemeData(
        primarySwatch: Colors.blue,
      ),
      home: const MyHomePage(title: 'Flutter Demo Home Page'),
    );
  }
}

class MyHomePage extends StatefulWidget {
  const MyHomePage({Key? key, required this.title}) : super(key: key);
  final String title;

  @override
  State<MyHomePage> createState() => _MyHomePageState();
}

class _MyHomePageState extends State<MyHomePage> {
  //定義webview的url
  final String url = "http://192.168.0.11/flutter";
  late InAppWebViewController _controller;

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        title: Text(widget.title),
      ),
      body: InAppWebView(
        initialUrlRequest: URLRequest(url: Uri.parse(url)),
        onWebViewCreated: (controller) {
          webController(controller);
        },
        onLoadStart: (controller, url) {},
        onLoadStop: (controller, url) {},
      ), // This trailing comma makes auto-formatting nicer for build methods.
    );
  }

  //webview的控制器
  InAppWebViewController webController(controller) {
    _controller = controller
      ..addJavaScriptHandler(
        //監聽web端傳入的值
        handlerName: 'to_js',
        callback: (data) {
          print("由js傳入的值: $data");
        },
      )
      ..addJavaScriptHandler(
        //監聽web端傳入的值
        handlerName: 'is_finish_load',
        callback: (data) {
          print("加載準備完成...");
          //傳值給web端
          String code = "window.receiveData('$url')";
          controller.evaluateJavascript(source: code).then((result) {});
          //傳list map給web端
          Map<String, dynamic> map1 = {
            "key": "122",
            "value": "ssss",
          };
          Map map2 = {
            "key": "taiwan",
            "value": "mmm",
          };
          List<Map> param = [map1, map2];
          String code2 = "window.receiveListMapData('$param')";
          controller.evaluateJavascript(source: code2).then((result) {});
        },
      );
    return _controller;
  }
}

