// 在Java中查找对象数组中元素索引的通用方法
public static<T> int find(T[] a, T target) {
    return Arrays.asList(a).indexOf(target);
}

class Person {
    private String name;
    private int age;

    public Person(String name, int age) {
        this.name = name;
        this.age = age;
    }

    public String getName() {
        return this.name;
    }

    public void setName(String name) {
        this.name = name;
    }

    public int getAge() {
        return this.age;
    }

    public void setAge(int age) {
        this.age = age;
    }
}

//反转数组
static void reverseArray(Object array) {
    Class cl = array.getClass();
    if (!cl.isArray()) return;
    int length = Array.getLength(array);

    int start = 0;
    int end = length - 1;
    while (start < end) {
        Object tempStart = Array.get(array, start);
        Object tempEnd = Array.get(array, end);
        Array.set(array, start, tempEnd);
        Array.set(array, end, tempStart);
        start++;
        end--;
    }
}

abstract class Person {
    public abstract void run();
}

class Student extends Person {
    @Override
    public void run() {
        System.out.println("Student.run");
    }
}

// interface 示例，比抽象类还要抽象的纯抽象接口，因为它连字段都不能有。
// 因为接口定义的所有方法默认都是public abstract的，所以这两个修饰符不需要写出来（写不写效果都一样）
interface Person {
    // 所有属性为类属性 编译器会自动加上public statc final:
    int MALE = 1;
    int FEMALE = 2;
    void run();

    String getName();
}

class Student implements Person {
    private String name;

    public Student(String name) {
        this.name = name;
    }

    @Override
    public void run() {
        System.out.println(this.name + " run");
    }

    @Override
    public String getName() {
        return this.name;
    }
}

