class GameQueryGameData {
  String? id;
  String? assortName;
  String? assortSort;

  GameQueryGameData({this.id,
    this.assortName,
    this.assortSort});

  GameQueryGameData.fromJson(Map<String, dynamic> json) {
    id = json['id'] is String ? json['id'] : '${json['id']}';
    assortName = json['assortName'];
    assortSort = json['assortSort'] is String ? json['assortSort'] : '${json['assortSort']}';
  }

  Map<String, dynamic> toJson() {
    final Map<String, dynamic> data = new Map<String, dynamic>();
    data['id'] = this.id;
    data['assortName'] = this.assortName;
    data['assortSort'] = this.assortSort;
    return data;
  }
}

