class $SetCurrencyModel$ extends ChangeNotifier {
  List<String> nameList = [];
   $SetCurrencyModel$() {
    nameList = ['CNY', 'VND'];
  }

  void setNameList(List<String> list){
     nameList = list;
     notifyListeners();
  }
}
